pixel = 10

pants = 1
shirt = 1
hair = 1

pants_color = 1
shirt_color = 1
hair_color = 1
shoe_color = 1
skin_color = 1
eye_color = 1
skin_color = 1

hairPrim = ""

pants1 = ["","","","","","","","",""]

pants2 = ["","","","","","","","",""]

shirt1 = ["","","","","","","","",""]

shirt2 = ["","","","","","","","",""]

hair1 = ["","","","","","","","",""]

hair2 = ["","","","","","","","",""]

body = ["","","","","","","","",""]

function update() {
  words = []
  for(let k = 0; k < 9; k++) {
    test = []

    for (i = 0; i < 32; i++) {
    test.push([])
    for (j = 0; j < 32; j++) {
      test[i].push("")
    }
  }

  for (i = 0; i < 32; i++) {
    for (j = 0; j < 32; j++) {
      if(body[k].charAt(i*32+j) == "1") {
        test[i][j] = getSkinColor(1,skin_color)
      }
      if(body[k].charAt(i*32+j) == "2") {
        test[i][j] = getSkinColor(2,skin_color)
      }
      if(body[k].charAt(i*32+j) == "3") {
        test[i][j] = getEyeColor(eye_color)
      }
      if(pants == 1) {
        if(pants1[k].charAt(i*32+j) == "1") {
          test[i][j] = getPantsColor(pants_color)
        }
        if(pants1[k].charAt(i*32+j) == "2") {
          test[i][j] = "#000000"
        }
      }
      else {
        if(pants2[k].charAt(i*32+j) == "1") {
          test[i][j] = getPantsColor(pants_color)
        }
        if(pants2[k].charAt(i*32+j) == "2") {
          test[i][j] = "#000000"
        }
      }
      if(shirt == 1) {
        if(shirt1[k].charAt(i*32+j) == "1") {
          test[i][j] = getShirtColor(shirt_color)
        }
      }
      else if(shirt == 2) {
        if(shirt2[k].charAt(i*32+j) == "1") {
          test[i][j] = getShirtColor(shirt_color)
        }
      }
      if(hair == 1) {
        if(hair1[k].charAt(i*32+j) == "1") {
          test[i][j] = getHairColor(hair_color)
        }
      }
      else if(hair == 2) {
        if(hair2[k].charAt(i*32+j) == "1") {
          test[i][j] = getHairColor(hair_color)
        }
      }
    }
  }

  word = ""

  for(let i = 0; i < test.length; i++) {
    for(let j=0; j < test[i].length; j++) {
      if(test[i][j] != "") {
        word +=  (j*pixel*.8) + "px " +((i+1)*pixel*.8) + "px " + test[i][j] + ", "
      }
    }
  }

  word = word.slice(0,-2)
  words.push(word)
}
}

update()

function hairUpdate() {
  hair = (hair+1)%3
  update()
}

function shirtUpdate() {
  shirt = (shirt+1)%3
  update()
}

function pantsUpdate() {
  pants = (pants+1)%2
  update()
}

function hairColorUpdate() {
  hair_color = (hair_color+1)%4
  update()
}

function getHairColor(index) {
  if(index == 0) {
    return "#3F1D00"
  }
  else if(index == 1) {
    return "#E4C795"
  }
  else if(index == 2) {
    return "#896148"
  }
  else if(index == 3) {
    return "#654224"
  }
}

function shirtColorUpdate() {
  shirt_color = (shirt_color+1)%4
  update()
}

function getShirtColor(index) {
  if(index == 0) {
    return "red"
  }
  else if(index == 1) {
    return "blue"
  }
  else if(index == 2) {
    return "green"
  }
  else if(index == 3) {
    return "purple"
  }
}

function pantsColorUpdate() {
  pants_color = (pants_color+1)%3
  update()
}

function getPantsColor(index) {
  if(index == 0) {
    return "black"
  }
  else if(index == 1) {
    return "gray"
  }
  else if(index == 2) {
    return "brown"
  }
}

function skinColorUpdate() {
  skin_color = (skin_color+1)%5
  update()
}

function getSkinColor(pix, index) {
  if(index == 0) {
    if(pix == 1) {
      return "#3b2219"
    }
    else {
      return "#1e110d"
    }
  }
  else if(index == 1) {
    if(pix == 1) {
      return "#a16e4b"
    }
    else {
      return "#744f36"
    }
  }
  else if(index == 2) {
    if(pix == 1) {
      return "#d4aa78"
    }
    else {
      return "#a3825a"
    }
  }
  else if(index == 3) {
    if(pix == 1) {
      return "#e6bc98"
    }
    else {
      return "#9f8269"
    }
  }
  else if(index == 4) {
    if(pix == 1) {
      return "#ffe7d1"
    }
    else {
      return "#a5917e"
    }
  }
}

function eyeColorUpdate() {
  eye_color = (eye_color+1)%3
  update()
}

function getEyeColor(index) {
  if(index == 0) {
    return "black"
  }
  else if(index == 1) {
    return "brown"
  }
  else if(index == 2) {
    return "green"
  }
}

