function pants(style) {
  if (style == "jeans") {
    return ["","","","","","","","",""]
  }
  else if (style == "shorts") {
    return ["","","","","","","","",""]
  }
  else {
    return null
  }
}

function shirts(style) {
  if (style == "long") {
    return ["","","","","","","","",""]
  }
  else if (style == "short") {
    return ["","","","","","","","",""]
  }
  else {
    return null
  }
}

function hair(style) {
  if(style == "long") {
    return ["","","","","","","","",""]
  }
  else if(style == "short") {
    return ["","","","","","","","",""]
  }
  else {
    return null
  }
}

function body() {
  return ["","","","","","","","",""]
}

pixel = 10

active_users = []
user_data = []

function newUser(name) {
  const data = {name: name}
  
  // Choose hair style and color
  
  let style = ["long", "short"]
  let color = ["#3F1D00", "#E4C795", "#896148", "#654224"]
  
  data.hair = {}
  data.hair.style = style[Math.floor(Math.random()*2)]
  data.hair.color = color[Math.floor(Math.random()*4)]

  // Choose pants style and color

  style = ["jeans", "shorts"]
  pants_color = ["black", "grey", "brown"]
  
  data.pants = {}
  data.pants.style = style[Math.floor(Math.random()*2)]
  data.pants.main_color = color[Math.floor(Math.random()*3)]
  data.pants.secondary_color = "#000000"

  // Choose shirt style and color

  style = ["long", "short"]
  color = ["red", "blue", "green", "purple"]

  data.shirt = {}
  data.shirt.style = style[Math.floor(Math.random()*2)]
  data.shirt.color = color[Math.floor(Math.random()*4)]

  // Choose body color

  skin = ["#3b2219","#a16e4b","#d4aa78","#e6bc98","#ffe7d1"]
  shadow = ["#1e110d","#744f36","#a3825a","#9f8269","#a5917e"]
  random = Math.floor(Math.random()*5)
  data.body = {}
  data.body.skin = skin[random]
  data.body.shadow = shadow[random]
  data.body.eyes = ["#000000"]

  // Add to global data
  
  user_data.push(data)
}


function makeFrames(name) {
  let frames_forward = []
  let frames_backward = []

  let index = null
  for (let i = 0; i < user_data.length; i++) {
    if (user_data[i].name = name) {
      index = i
    }
  }

  let body_mask = body()
  let hair_mask = hair(user_data[index].hair.style)
  let shirt_mask = shirts(user_data[index].shirt.style)
  let pants_mask = pants(user_data[index].pants.style)
  
  for(let k = 0; k < 9; k++) {
    ascii = []

    for (let i = 0; i < 32; i++) {
      ascii.push([])
      for (j = 0; j < 32; j++) {
        ascii[i].push("")
      }
    }

    for (let i = 0; i < 32; i++) {
      for (let j = 0; j < 32; j++) {
        if(body_mask[k].charAt(i*32+j) == "1") {
          ascii[i][j] = user_data[index].body.skin
        }
        if(body_mask[k].charAt(i*32+j) == "2") {
          ascii[i][j] = user_data[index].body.shadow
        }
        if(body_mask[k].charAt(i*32+j) == "3") {
          ascii[i][j] = user_data[index].body.eyes
        }
        if(pants_mask[k].charAt(i*32+j) == "1") {
          ascii[i][j] = user_data[index].pants.main_color
        }
        if(pants_mask[k].charAt(i*32+j) == "2") {
          ascii[i][j] = user_data[index].pants.secondary_color
        }
        if(shirt_mask[k].charAt(i*32+j) == "1") {
          ascii[i][j] = user_data[index].shirt.color
        }
        if(hair_mask[k].charAt(i*32+j) == "1") {
          ascii[i][j] = user_data[index].hair.color
        }
      }
    }

    let frame_forward = ""
    let frame_backward = ""

    for(let i = 0; i < ascii.length; i++) {
      for(let j=0; j < ascii[i].length; j++) {
        if(ascii[i][j] != "") {
          frame_forward +=  (j*pixel*.8) + "px " +((i+1)*pixel*.8) + "px " + ascii[i][j] + ", "
          frame_backward +=  ((32-j)*pixel*.8) + "px " +((i+1)*pixel*.8) + "px " + ascii[i][j] + ", "
        }
      }
    }
    frame_forward = frame_forward.slice(0,-2)
    frames_forward.push(frame_forward)
    frame_backward = frame_backward.slice(0,-2)
    frames_backward.push(frame_backward)
  }
  user_data[index].animation ={}
  user_data[index].animation.forwards = frames_forward
  user_data[index].animation.backwards = frames_backward
}

newUser("bob")
makeFrames("bob")

